void ssd1306_update(uint8_t buf[8][128]){
    for (uint8_t page = 0; page < 8; page++)
    {
        ssd1306_cmd(0xB0 + page); //page
        ssd1306_cmd(0x00); //lower column
        ssd1306_cmd(0x10); //higher column

        ssd1306_data(buf[page], 128);
    }
    
}



